mapper.update:
update goods
set goodName = #{goodName,jdbcType=VARCHAR},
  goodNum = #{goodNum,jdbcType=INTEGER},
  editTime = #{editTime,jdbcType=TIMESTAMP},
  editUser = #{editUser,jdbcType=VARCHAR}
where goodId = #{goodId,jdbcType=BIGINT}

mapper.select:
select goodId, goodName, goodNum, editTime, editUser
from goods
where goodId = #{goodId,jdbcType=BIGINT}
