mapper.select:
select id, company, username, password, name, phone, sex, email, update_time, status,deptName,login_count
from user
where id = #{id,jdbcType=BIGINT}

select id, company, username, password, name, phone, sex, email, update_time, status,deptName,login_count
from user
where username=#{username}

select id, company, username, password, name, phone, sex, email, update_time, status,deptName,login_count from user
and username like concat('%',#{username},'%')
and phone =#{phone}
