mapper.insert:
insert into variable (varId, varLable, varName, 
  varValue, editTime, editUser
  )
values (#{varId,jdbcType=INTEGER}, #{varLable,jdbcType=VARCHAR}, #{varName,jdbcType=VARCHAR},
  #{varValue,jdbcType=VARCHAR}, #{editTime,jdbcType=TIMESTAMP}, #{editUser,jdbcType=VARCHAR}
  )

mapper.select:
select varId, varLable, varName, varValue, editTime, editUser
from variable
where varId = #{varId,jdbcType=INTEGER}

select varId, varLable, varName, varValue, editTime, editUser from variable
and varName like concat('%',#{varName},'%')
and varLable =#{varLable}
